const fetch = require('node-fetch');

const getGif = (type) => {
    var promise = new Promise(async function(resolve, reject) {
        let { url } = await fetch(`https://api.satou-chan.xyz/api/endpoint/${type}`).then(body => body.json());

        resolve(url);
    });
    return promise;
}


exports.getGif = getGif;